import React ,{useState} from 'react'
import {Form, Button} from 'react-bootstrap'


 const SearchBox = ({history}) => {
   const [keyword,setKeyword] = useState('')

  const submitHandler=(e) => {
    e.preventDefault()
    if (keyword.trim()){ //ive never seen people calling a function inside the () in the if statement
       history.push(`/search/${keyword}`)
    }else{

      history.push('/')
    }
  }

       return (
        <Form onSubmit={submitHandler} inline>
         <Form.Control type='text' name='q' onChange={(e)=>setKeyword(e.target.value)} placeholder='Search Products...' className="mr-sm-2 ml-sm-5">
         </Form.Control>
         <Button variant='outline-success' type="submit" className='p-2' >
           Search
          </Button>
        </Form>


 )
}





export default SearchBox
